                private static Random _random;
                private static List<string> _hamletdata;
                private static int _hamletlength;
                private static List<string> _beowulfdata;
                private static int _beowulflength;

                static ObjectGenerator() {
                    _hamletdata = new List<String>(_rawHamlet.Split('' ''));
                    _hamletlength = _hamletdata.Count;
                    _beowulfdata = new List<String>(_rawBeowulf.Split('' ''));
                    _beowulflength = _beowulfdata.Count;
                    _random = new Random(DateTime.Now.Millisecond);
                }

                private static string Run(int count, string mode, bool subsequent = false) {
                    if(mode == "hamlet") {
                        return _hamletdata[_random.Next(1, _hamletlength)] + (count == 0 ? "" : " " + Run(count - 1, mode, true));
                    }
                    else {
                        return _beowulfdata[_random.Next(1, _beowulflength)] + (count == 0 ? "" : " " + Run(count - 1, mode, true));
                    }
                }

                private static dynamic CreateItem(string mode = "hamlet") {
                    var item = new {
                        Id = "9780" + _random.Next(100000000, 999999999),
                        Title = Run(_random.Next(4, 8), mode),
                        Authors = Enumerable.Range(1, 4).Select(p => Run(2, mode)).ToList(),
                        Metadata = new {
                            Pages = _random.Next(1, 400),
                            Genre = Enumerable.Range(1, 2).Select(p => Run(1, mode)).ToList(),
                            Summary = Run(_random.Next(100, 400), mode),
                        },
                        Published = new DateTime(_random.Next(1960, 2016), _random.Next(1, 12), _random.Next(1, 28)),
                        Editor = _random.Next(1, 4) == 1 ? Run(1, mode) : null
                    };

                    return item;
                }

                public static List<dynamic> _Run(int count, string mode) {
                    var bulk = new List<dynamic>();
                    for (var n = 0; n < count; n++) {
                        bulk.Add(CreateItem(mode));
                    }
                    return bulk;
                }

                public static List<dynamic> Beowulf(int count) {
                    return _Run(count, "beowulf");
                }

                public static List<dynamic> Hamlet(int count, string mode = "hamlet") {
                    return _Run(count, "hamlet");
                }
            }
        }

